import {createStore} from "vuex";

export default createStore({
    state:{
        user:null,
        saltKey:"booklike123Q456"
    },
    getters:{
        _isAuthenticated:state=>state.user !=null,
        _getCurrentUser(state){
         const user=state.user;
         delete user?.password;//user içerisinde password idye bir alan varsa bunu sil 
         return user;
        }
    }
    //optional chaining
    //hata olması durumunda  ? ile çözülüyo
